import React, { useEffect, useState } from 'react'

import { StyleSheet, Text, View, SafeAreaView, Button, FlatList, Image } from 'react-native';


import api from '../../api/index'

const Lista = () => {

    const [dados, setDados] = useState([]);

    // teste de input
    useEffect(() => {
        api.get('/aluno').then(res => {
            console.log(res.data)
            setDados(res.data)
        })
    }, [])

    const getdata = () => {
        console.log('dados: ', dados)
    }


    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>
                <Text>dados</Text>
                <View style={styles.container}>


                </View>
                <Button
                    title="getdata"
                    onPress={getdata}
                />
                <FlatList
                    data={dados}
                    renderItem={({ item }) => (
                        <View>
                            <Image
                                style={styles.tinyLogo}
                                source={{
                                    uri: item.image,
                                }}
                            />

                            <Button
                                style={styles.btnExcluir}
                                title="deletar"
                                onPress={() => submit()}
                            />
                            <Button
                                title="Editar"
                                onPress={() => submit()}
                            />
                            <Text>{item.name}</Text>
                            <Text>{item.endereco}</Text>
                        </View>
                    )}
                    keyExtractor={dados => dados._id}
                />

            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#333',
    },
    tinyLogo: {
        width: 50,
        height: 50,
    },
    logo: {
        width: 66,
        height: 58,
    },
    btnExcluir: {

    }
});



export default Lista
