import React, { useEffect, useState } from 'react'

import { StyleSheet, Text, View, SafeAreaView, Button, FlatList, Image } from 'react-native';


import api from '../../api/index'

const Lista = () => {

    const [dados, setDados] = useState([]);

    // teste de input
    useEffect(() => {
        api.get('/aluno').then(res => {
            console.log(res.data)
            setDados(res.data)
        })
    }, [])

    const getdata = () => {
        console.log('dados: ', dados)
    }


    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>
                <Text>dados</Text>
                <View style={styles.container}>


                </View>
                <Button
                    title="getdata"
                    onPress={getdata}
                />
                <FlatList
                    data={dados}
                    renderItem={({ item }) => (
                        <View>
                            <View style={styles.card}>
                                <Image
                                    style={styles.tinyLogo}
                                    source={{
                                        uri: item.image,
                                    }}
                                />
                                <View style={styles.nomes}>
                                    <Text>Nome: {item.name}</Text>
                                    <Text>Endereço: {item.endereco}</Text>
                                </View>
                            </View>
                            <View style={styles.botaos}>
                                <View style={styles.btnExcluir}>
                                    <Button
                                        color="tomato"
                                        title="deletar"
                                        onPress={() => submit()}
                                    />
                                </View>
                                <View style={styles.btnEditar}>
                                    <Button
                                        color="gold"
                                        title="Editar"
                                        onPress={() => submit()}
                                    />
                                </View>
                            </View>
                        </View>
                    )}
                    keyExtractor={dados => dados._id}
                />

            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#333',
    },
    card: {
        flexDirection: 'row',
        textAlign: 'start',
        alignItems: 'center',
        justifyContent: 'center',
    },
    nomes: {
        flexDirection: 'column',
        padding: 10,
    },
    tinyLogo: {
        width: 50,
        height: 50,
    },
    botaos: {
        margin: 10,
        flexDirection: 'row'
    },

    btnExcluir: {
        margin: 10,
        alignItems: 'center',

    },

    btnEditar: {
        margin: 10,
        alignItems: 'center',


    }

});



export default Lista
