import React, { useEffect, useState } from 'react'

import { StyleSheet, Text, View, SafeAreaView, Button, FlatList, Image, Modal } from 'react-native';


import api from '../../api/index'

const Lista = () => {

    const [dados, setDados] = useState();
    const [name, setName] = useState('')
    const [endereco, setEndereco] = useState('');
    const [modalVisible, setModalVisible] = useState(false);
    // teste de input
    useEffect(() => {
        api.get('/aluno').then(res => {
            console.log(res.data)
            setDados(res.data)
        })
    }, [])


    const deletar = (index) => {
        console.log('index deletar: ', index)
        api.delete(`/aluno/${index}`).then(res => {
            console.log(res.data)
        })
    }
    const editar = (index) => {
        console.log('dados deletar: ', index)
        setModalVisible(!modalVisible)
        const data = {
            name: name,
            endereco: endereco,
            // file: image
        }
        api.put(`/aluno/${index}`, data).then(res => {
            console.log(res.data)
        })
    }


    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>


                <FlatList
                    data={dados}
                    renderItem={({ item }) => (
                        <View>
                            <View style={styles.card}>
                                <Image
                                    style={styles.tinyLogo}
                                    source={{
                                        uri: item.image,
                                    }}
                                />
                                <View style={styles.nomes}>
                                    <Text>Nome: {item.name}</Text>
                                    <Text>Endereço: {item.endereco}</Text>
                                </View>
                            </View>
                            <View style={styles.botaos}>
                                <View style={styles.btnExcluir}>
                                    <Button
                                        color="tomato"
                                        title="deletar"
                                        onPress={() => deletar(item._id)}
                                    />
                                </View>
                                <View style={styles.btnEditar}>
                                    <Button

                                        color="gold"
                                        title="Editar"
                                        onPress={() => editar(item._id)}
                                    />
                                    <Modal
                                        animationType="slide"
                                        transparent={true}
                                        visible={modalVisible}
                                        onRequestClose={() => {
                                            Alert.alert("Modal has been closed.");
                                        }}
                                    ></Modal>
                                </View>
                            </View>
                        </View>
                    )}
                    keyExtractor={dados => dados._id}
                />

            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    teste: {
        display: 'none'
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#333',
    },
    card: {
        flexDirection: 'row',
        textAlign: 'start',
        alignItems: 'center',

        backgroundColor: '#f1f1f1',

    },
    nomes: {
        flexDirection: 'column',
        padding: 10,
    },
    tinyLogo: {
        width: 50,
        height: 50,
    },
    botaos: {

        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 50,
    },

    btnExcluir: {
        margin: 10,
        alignItems: 'center',

    },

    btnEditar: {
        margin: 10,
        alignItems: 'center',


    }

});



export default Lista
