import React, { useEffect, useState } from 'react'

import { StyleSheet, Text, View, SafeAreaView, Button, FlatList, Image, TextInput } from 'react-native';


import api from '../../api/index'

const Lista = () => {

    const [dados, setDados] = useState();
    const [name, setName] = useState('')
    const [endereco, setEndereco] = useState('');
    const [x, setX] = useState(false);

    // teste de input
    useEffect(() => {
        api.get('/aluno').then(res => {
            console.log(res.data)
            setDados(res.data)
        })
    }, [])


    const deletar = (index) => {
        console.log('index deletar: ', index)
        api.delete(`/aluno/${index}`).then(res => {
            console.log(res.data)
        })
    }
    const editar = (index) => {
        console.log('dados deletar: ', index)

        const data = {
            name: name,
            endereco: endereco,
            // file: image
        }
        api.put(`/aluno/${index}`, data).then(res => {
            console.log(res.data)
        })
        setX(true)
    }

    if (x) {
        return <View style={styles.container}>
            <Text>holly moly</Text>
            <TextInput
                style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                onChangeText={text => setName(text)}
                value={name}
            />
            <TextInput
                style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                onChangeText={text => setEndereco(text)}
                value={endereco}
            />
            <Button
                title="Press me"
                onPress={() => submit()}
            />
        </View>
    }


    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>


                <FlatList
                    data={dados}
                    renderItem={({ item }) => (
                        <View>
                            <View style={styles.card}>
                                <Image
                                    style={styles.tinyLogo}
                                    source={{
                                        uri: item.image,
                                    }}
                                />
                                <View style={styles.nomes}>
                                    <Text>Nome: {item.name}</Text>
                                    <Text>Endereço: {item.endereco}</Text>
                                </View>
                            </View>
                            <View style={styles.botaos}>
                                <View style={styles.btnExcluir}>
                                    <Button
                                        color="tomato"
                                        title="deletar"
                                        onPress={() => deletar(item._id)}
                                    />
                                </View>
                                <View style={styles.btnEditar}>
                                    <Button

                                        color="gold"
                                        title="Editar"
                                        onPress={() => editar(item._id)}
                                    />

                                </View>
                            </View>
                            {x}
                        </View>
                    )}
                    keyExtractor={dados => dados._id}
                />





            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    teste: {
        display: 'none'
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#333',
    },
    card: {
        flexDirection: 'row',
        textAlign: 'start',
        alignItems: 'center',

        backgroundColor: '#f1f1f1',

    },
    nomes: {
        flexDirection: 'column',
        padding: 10,
    },
    tinyLogo: {
        width: 50,
        height: 50,
    },
    botaos: {

        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 50,
    },

    btnExcluir: {
        margin: 10,
        alignItems: 'center',

    },

    btnEditar: {
        margin: 10,
        alignItems: 'center',


    }

});



export default Lista
