import React, { useEffect, useState } from 'react'

import * as ImagePicker from "expo-image-picker";
import { StyleSheet, Text, View, SafeAreaView, Button, FlatList, Image, TextInput } from 'react-native';

import api from '../../api/index'

const Lista = ({ navigation }) => {

    const [dados, setDados] = useState();
    const [name, setName] = useState('')
    const [endereco, setEndereco] = useState('');
    const [image, setImage] = useState(null);
    const [id, setId] = useState('');
    // open  edit
    const [openEdit, setOpenEdit] = useState(false);

    // teste de input
    useEffect(() => {
        api.get('/aluno').then(res => {
            console.log(res.data)
            setDados(res.data)
        })
    }, [])

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            includeBase64: false,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
        });
        console.log(result)


        if (!result.cancelled) {
            setImage(result.uri);
        }
    };


    const deletar = (index) => {
        console.log('index deletar: ', index)
        api.delete(`/aluno/${index}`).then(res => {
            console.log(res.data)
        })
    }

    const edit = (index) => {
        console.log('dados deletar: ', index)
        setId(index)
        setOpenEdit(true)
    }
    const editar = async () => {

        const data = {
            name: name,
            endereco: endereco,
            // file: image
        }
        await api.put(`/aluno/${id}`, data).then(res => {
            console.log("editado")
            console.log(res.data)
        })
        setOpenEdit(false)

    }
    console.log(endereco)
    console.log(name)


    if (openEdit) {
        return <View style={styles.container}>

            <View style={styles.container}>
                <Button title="Go back" onPress={() => navigation.goBack()} />
            </View>
            <Text style={{ fontSize: 30 }}>Editar</Text>
            <Text>Nome</Text>
            <TextInput
                style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                onChangeText={text => setName(text)}
                value={name}
            />
            <Text>Endereço</Text>
            <TextInput
                style={{ height: 40, borderColor: 'gray', borderWidth: 1, margin: 10 }}
                onChangeText={text => setEndereco(text)}
                value={endereco}
            />
            <View style={styles.botaos}>
                <View style={styles.btnExcluir}>
                    <Button
                        color="tomato"
                        title="Cancelar"
                        onPress={() => setOpenEdit(false)}
                    />
                </View>
                <View style={styles.btnEditar}>
                    <Button
                        onPress={() => editar()}
                        color="green"
                        title="Confirmar"

                    />

                </View>
            </View>
        </View>
    }


    return (
        <SafeAreaView style={styles.container}>

            <View style={styles.container}>


                <FlatList
                    data={dados}
                    renderItem={({ item }) => (
                        <View>
                            <View style={styles.card}>
                                <Image
                                    style={{ width: 50, height: 50 }}
                                    source={{
                                        uri: item.image,
                                    }}
                                />
                                <View style={styles.nomes}>
                                    <Text>Nome: {item.name}</Text>
                                    <Text>Endereço: {item.endereco}</Text>
                                </View>
                            </View>
                            <View style={styles.botaos}>
                                <View style={styles.btnExcluir}>
                                    <Button
                                        color="tomato"
                                        title="deletar"
                                        onPress={() => deletar(item._id)}
                                    />
                                </View>
                                <View style={styles.btnEditar}>
                                    <Button
                                        color="gold"
                                        title="Editar"
                                        onPress={() => edit(item._id)}
                                    />

                                </View>
                                <View style={styles.btnImg}>
                                    <Button
                                        color="#333"
                                        title="Coloque sua imagem" onPress={pickImage} />
                                    {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
                                </View>
                            </View>

                        </View>
                    )}
                    keyExtractor={dados => dados._id}
                />





            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    teste: {
        display: 'none'
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#333',
    },
    card: {
        flexDirection: 'row',
        textAlign: 'start',
        alignItems: 'center',

        backgroundColor: '#f1f1f1',

    },
    nomes: {
        flexDirection: 'column',
        padding: 10,
    },

    botaos: {

        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 50,
    },

    btnExcluir: {
        margin: 10,
        alignItems: 'center',

    },

    btnEditar: {
        margin: 10,
        alignItems: 'center',


    }

});



export default Lista
