import React, { useEffect, useState, useCallback } from 'react'

import * as ImagePicker from "expo-image-picker";
import { StyleSheet, Text, View, TextInput, Button, SafeAreaView, TouchableOpacity } from 'react-native';


import api from '../../api/index'

const Crud = () => {
    const [name, setName] = useState('')
    const [endereco, setEndereco] = useState('');
    const [image, setImage] = useState();



    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
        });

        if (!result.cancelled) {
            setImage(result.uri);
        }
    };


    // submit 
    const submit = () => {
        const data = {
            name: name,
            endereco: endereco
        }
        console.log("Summited", data)

        api.post('/', data).then(res => {
            console.log(res)
        })
        console.log(image)
    }

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>
                <Text>holly moly</Text>
                <TextInput
                    style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                    onChangeText={text => setName(text)}
                    value={name}
                />
                <TextInput
                    style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                    onChangeText={text => setEndereco(text)}
                    value={endereco}
                />

                <Button title="Coloque sua imagem" onPress={pickImage} />

                <Button
                    title="Press me"
                    onPress={() => submit()}
                />
            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default Crud
