import React, { useEffect, useState } from 'react'
import { StyleSheet, Text, View, TextInput, Button, SafeAreaView, ImageInput } from 'react-native';


const Crud = () => {
    const [name, setName] = useState('')
    const [endereco, setEndereco] = useState('');

    //image change
    const [file, setFile] = useState(
        "https://siead.faculdadefamart.edu.br/content/timthumb.php?src=https://siead.faculdadefamart.edu.br/content/upload/usuario/371bce7dc83817b7893bcdeed13799b5.jpg&h=160&w=160"
    );
    const handleChangeImage = (event) => {
        setFile(URL.createObjectURL(event.target.files[0]));
    };

    // submit 
    const submit = () => {
        const data = {
            name: name,
            endereco: endereco
        }
        console.log("Summited", data)
    }
    // teste de input
    useEffect(() => {
        console.log("Name: ", name)
        console.log("eNDERECO: ", endereco)
    }, [name, endereco])

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container}>
                <Text>holly moly</Text>
                <TextInput
                    style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                    onChangeText={text => setName(text)}
                    value={name}
                />
                <TextInput
                    style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                    onChangeText={text => setEndereco(text)}
                    value={endereco}
                />
                <ImageInput />


                <Button
                    title="Press me"
                    onPress={() => submit()}
                />
            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default Crud
